mapper.select:
select id
from USERS
where id=#{id} AND pwd=#{pwd}

select id
from USERS
where id=#{id}

mapper.insert:
    	INSERT INTO USERS ( 
    		id
    		, pwd
    		, regdate
    		, email
    		, care 
    	) 
    	VALUES ( 
			#{id}
			, #{pwd}
			, now()
			, #{email}
			, #{care} 
		)

mapper.update:
UPDATE USERS
SET profileImg=#{profile}
WHERE id=#{id}

UPDATE USERS
SET pwd=#{newPwd}
WHERE id=#{id} AND pwd=#{pwd}

UPDATE USERS
SET email=#{email}, care=#{care}
WHERE id=#{id}

mapper.select:
SELECT pwd
FROM USERS
WHERE id=#{id}

mapper.update:
